// src/components/StoryList.tsx
import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import { Story, getStories, createStory, updateStory, deleteStory } from '../api/storyService';

const StoryList: React.FC = () => {
  const [stories, setStories] = useState<Story[]>([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [editingStory, setEditingStory] = useState<Story | null>(null);

  useEffect(() => {
    fetchStories();
  }, []);

  const fetchStories = async () => {
    try {
      const data = await getStories();
      setStories(data);
    } catch (error) {
      console.error('Error fetching stories:', error);
    }
  };

  const handleCreateStory = async (story: Story) => {
    try {
      const createdStory = await createStory(story);
      setStories([...stories, createdStory]);
      closeModal();
    } catch (error) {
      console.error('Error creating story:', error);
    }
  };

  const handleUpdateStory = async (id: string, updatedStory: Story) => {
    try {
      const updated = await updateStory(id, updatedStory);
      const updatedStories = stories.map((story) => (story._id === updated._id ? updated : story));
      setStories(updatedStories);
      closeModal();
    } catch (error) {
      console.error('Error updating story:', error);
    }
  };

  const handleDeleteStory = async (id: string) => {
    try {
      await deleteStory(id);
      const updatedStories = stories.filter((story) => story._id !== id);
      setStories(updatedStories);
    } catch (error) {
      console.error('Error deleting story:', error);
    }
  };

  const openModal = (story: Story | null = null) => {
    setEditingStory(story);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setEditingStory(null);
    setModalIsOpen(false);
  };

  const handleShowDetails = (story: Story) => {
    console.log(story); // Możesz dodać logikę do wyświetlania szczegółów w modalu
    openModal(story); // Otwieranie modala z detalami historii
  };

  const handleSetActiveStory = async (id: string) => {
    try {
      const updatedStory = await updateStory(id, { ...editingStory!, active: true }); // Przykładowa implementacja ustawiania aktywnej historii
      const updatedStories = stories.map((story) => (story._id === updatedStory._id ? updatedStory : story));
      setStories(updatedStories);
    } catch (error) {
      console.error('Error setting active story:', error);
    }
  };

  return (
    <div className="container mx-auto">
      <h1 className="text-2xl font-bold mt-4 mb-8">Story List</h1>

      {/* Kanban board */}
      <div className="grid grid-cols-3 gap-4 mb-8">
        <div>
          <h2>Todo</h2>
          {stories.filter((story) => story.status === 'todo').map((story) => (
            <div key={story._id} className="border p-2 mb-2 bg-gray-200">
              <strong>{story.name}</strong>: {story.description}
              <button onClick={() => openModal(story)} className="bg-yellow-500 text-white p-2 ml-2 rounded">
                Edit
              </button>
              <button onClick={() => handleDeleteStory(story._id!)} className="bg-red-500 text-white p-2 ml-2 rounded">
                Delete
              </button>
              <button onClick={() => handleShowDetails(story)} className="bg-green-500 text-white p-2 ml-2 rounded">
                Details
              </button>
              <button
                onClick={() => handleSetActiveStory(story._id!)}
                className={`p-2 ml-2 rounded ${story.active ? 'bg-green-500' : 'bg-gray-500'} text-white`}
              >
                {story.active ? 'Selected' : 'Select'}
              </button>
            </div>
          ))}
        </div>
        <div>
          <h2>Doing</h2>
          {stories.filter((story) => story.status === 'doing').map((story) => (
            <div key={story._id} className="border p-2 mb-2 bg-gray-200">
              <strong>{story.name}</strong>: {story.description}
              <button onClick={() => openModal(story)} className="bg-yellow-500 text-white p-2 ml-2 rounded">
                Edit
              </button>
              <button onClick={() => handleDeleteStory(story._id!)} className="bg-red-500 text-white p-2 ml-2 rounded">
                Delete
              </button>
              <button onClick={() => handleShowDetails(story)} className="bg-green-500 text-white p-2 ml-2 rounded">
                Details
              </button>
              <button
                onClick={() => handleSetActiveStory(story._id!)}
                className={`p-2 ml-2 rounded ${story.active ? 'bg-green-500' : 'bg-gray-500'} text-white`}
              >
                {story.active ? 'Selected' : 'Select'}
              </button>
            </div>
          ))}
        </div>
        <div>
          <h2>Done</h2>
          {stories.filter((story) => story.status === 'done').map((story) => (
            <div key={story._id} className="border p-2 mb-2 bg-gray-200">
              <strong>{story.name}</strong>: {story.description}
              <button onClick={() => openModal(story)} className="bg-yellow-500 text-white p-2 ml-2 rounded">
                Edit
              </button>
              <button onClick={() => handleDeleteStory(story._id!)} className="bg-red-500 text-white p-2 ml-2 rounded">
                Delete
              </button>
              <button onClick={() => handleShowDetails(story)} className="bg-green-500 text-white p-2 ml-2 rounded">
                Details
              </button>
              <button
                onClick={() => handleSetActiveStory(story._id!)}
                className={`p-2 ml-2 rounded ${story.active ? 'bg-green-500' : 'bg-gray-500'} text-white`}
              >
                {story.active ? 'Selected' : 'Select'}
              </button>
            </div>
          ))}
        </div>
      </div>

      {/* Modal for editing and displaying details */}
      <Modal isOpen={modalIsOpen} onRequestClose={closeModal} className="Modal" overlayClassName="Overlay">
        <div className="bg-white p-4 rounded shadow-lg">
          <h2 className="text-xl font-bold mb-4">{editingStory ? 'Edit Story' : 'Story Details'}</h2>
          {editingStory && (
            <form onSubmit={(e) => {
              e.preventDefault();
              if (editingStory._id) {
                handleUpdateStory(editingStory._id, editingStory);
              } else {
                handleCreateStory(editingStory);
              }
            }}>
              <div className="mb-4">
                <label className="block text-gray-700 font-bold mb-2">Name</label>
                <input
                  type="text"
                  value={editingStory.name}
                  onChange={(e) => setEditingStory({ ...editingStory, name: e.target.value })}
                  className="border p-2 w-full"
                />
              </div>
              <div className="mb-4">
                <label className="block text-gray-700 font-bold mb-2">Description</label>
                <textarea
                  value={editingStory.description}
                  onChange={(e) => setEditingStory({ ...editingStory, description: e.target.value })}
                  className="border p-2 w-full h-24 resize-none"
                />
              </div>
              <div className="mb-4">
                <label className="block text-gray-700 font-bold mb-2">Priority</label>
                <select
                  value={editingStory.priority}
                  onChange={(e) => setEditingStory({ ...editingStory, priority: e.target.value as 'low' | 'medium' | 'high' })}
                  className="border p-2 w-full"
                >
                  <option value="low">Low</option>
                  <option value="medium">Medium</option>
                  <option value="high">High</option>
                </select>
              </div>
              <div className="mb-4">
                <label className="block text-gray-700 font-bold mb-2">Status</label>
                <select
                  value={editingStory.status}
                  onChange={(e) => setEditingStory({ ...editingStory, status: e.target.value as 'todo' | 'doing' | 'done' })}
                  className="border p-2 w-full"
                >
                  <option value="todo">Todo</option>
                  <option value="doing">Doing</option>
                  <option value="done">Done</option>
                </select>
              </div>
              <button type="submit" className="bg-blue-500 text-white p-2 rounded">
                Save Changes
              </button>
            </form>
          )}
          {!editingStory && (
            <>
              <p><strong>Name:</strong> {editingStory?.name}</p>
              <p><strong>Description:</strong> {editingStory?.description}</p>
              <p><strong>Priority:</strong> {editingStory?.priority}</p>
              <p><strong>Status:</strong> {editingStory?.status}</p>
            </>
          )}
          <button onClick={closeModal} className="bg-gray-500 text-white p-2 mt-4 rounded">
            Close
          </button>
        </div>
      </Modal>
    </div>
  );
};

export default StoryList;
