import axios from 'axios';

export interface Story {
  _id?: string; // _id może być opcjonalne przy dodawaniu nowej historii
  name: string;
  description: string;
  priority: 'low' | 'medium' | 'high';
  project: number;
  createdAt?: Date; // createdAt może być opcjonalne przy dodawaniu nowej historii
  status: 'todo' | 'doing' | 'done';
  ownerId: number;
}

const API_URL = 'http://localhost:5000/stories';

export const getAll = async (): Promise<Story[]> => {
  try {
    const response = await axios.get<Story[]>(API_URL);
    return response.data;
  } catch (error) {
    console.error('Error fetching stories:', error);
    throw error;
  }
};

export const add = async (story: Story): Promise<void> => {
  try {
    await axios.post(API_URL, story);
  } catch (error) {
    console.error('Error adding story:', error);
    throw error;
  }
};

export const update = async (updatedStory: Story): Promise<void> => {
  try {
    await axios.put(`${API_URL}/${updatedStory._id}`, updatedStory);
  } catch (error) {
    console.error('Error updating story:', error);
    throw error;
  }
};

export const remove = async (storyId: string): Promise<void> => {
  try {
    await axios.delete(`${API_URL}/${storyId}`);
  } catch (error) {
    console.error('Error deleting story:', error);
    throw error;
  }
};

export const getById = async (storyId: string): Promise<Story | undefined> => {
  try {
    const response = await axios.get<Story>(`${API_URL}/${storyId}`);
    return response.data;
  } catch (error) {
    console.error('Error fetching story by ID:', error);
    throw error;
  }
};
