// src/services/storyService.ts

export interface Story {
    id: number;
    name: string;
    description: string;
    priority: 'low' | 'medium' | 'high';
    project: number;
    createdAt: Date;
    status: 'todo' | 'doing' | 'done';
    ownerId: number;
  }

  const API_URL = 'http://localhost:5000/stories';

export const getAll = async (): Promise<Story[]> => {
  const response = await fetch(API_URL);
  const stories = await response.json();
  return stories;
};

export const add = async (story: Story): Promise<void> => {
  await fetch(API_URL, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(story),
  });
};

export const update = async (updatedStory: Story): Promise<void> => {
  await fetch(`${API_URL}/${updatedStory.id}`, {
    method: 'PUT',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(updatedStory),
  });
};

export const remove = async (storyId: number): Promise<void> => {
  await fetch(`${API_URL}/${storyId}`, {
    method: 'DELETE',
  });
};

export const getById = async (storyId: number): Promise<Story | undefined> => {
  const response = await fetch(`${API_URL}/${storyId}`);
  const story = await response.json();
  return story;
};
