import React, { useEffect, useState } from 'react';
import { getProjects, createProject, updateProject, deleteProject, Project } from '../api/projectService';
import '../style/projects.css';
import Modal from 'react-modal';

const App: React.FC = () => {
  const [projects, setProjects] = useState<Project[]>([]);
  const [currentProject, setCurrentProject] = useState<Project | null>(null);
  const [modalVisible, setModalVisible] = useState(false);

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const projects = await getProjects();
      setProjects(projects);
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };

  const handleCreateProject = async (e: React.FormEvent) => {
    e.preventDefault();
    const form = e.target as HTMLFormElement;
    const name = (form.elements.namedItem('name') as HTMLInputElement).value;
    const description = (form.elements.namedItem('description') as HTMLInputElement).value;
    try {
      const newProject = await createProject({ name, description });
      setProjects([...projects, newProject]);
      form.reset();
    } catch (error) {
      console.error('Error creating project:', error);
    }
  };

  const handleUpdateProject = async () => {
    if (currentProject && currentProject._id) {
      try {
        const updatedProject = await updateProject(currentProject._id, currentProject);
        setProjects(projects.map(p => (p._id === currentProject._id ? updatedProject : p)));
        setModalVisible(false);
        setCurrentProject(null);
      } catch (error) {
        console.error('Error updating project:', error);
      }
    }
  };

  const handleDeleteProject = async (id: string) => {
    try {
      await deleteProject(id);
      setProjects(projects.filter(p => p._id !== id));
    } catch (error) {
      console.error('Error deleting project:', error);
    }
  };

  const openUpdateModal = (project: Project) => {
    setCurrentProject(project);
    setModalVisible(true);
  };

  return (
    <div>
      <h1>Projects</h1>
      <form onSubmit={handleCreateProject}>
        <input type="text" name="name" placeholder="Project Name" required />
        <input type="text" name="description" placeholder="Description" required />
        <button type="submit">Add Project</button>
      </form>
      <ul>
        {projects.map(project => (
          <li key={project._id}>
            {project.name}: {project.description}
            <button onClick={() => openUpdateModal(project)}>Edit</button>
            <button onClick={() => handleDeleteProject(project._id!)}>Delete</button>
          </li>
        ))}
      </ul>
      {modalVisible && currentProject && (
        <div>
          <h2>Edit Project</h2>
          <input
            type="text"
            value={currentProject.name}
            onChange={e => setCurrentProject({ ...currentProject, name: e.target.value })}
          />
          <input
            type="text"
            value={currentProject.description}
            onChange={e => setCurrentProject({ ...currentProject, description: e.target.value })}
          />
          <button onClick={handleUpdateProject}>Save</button>
          <button onClick={() => setModalVisible(false)}>Cancel</button>
        </div>
      )}
    </div>
  );
};

export default App;
