import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import { Story, getStories, createStory, updateStory, deleteStory } from '../api/storyService';
import { Project, getProjects } from '../api/projectService';
import { User, getUsers } from '../api/userService';
import NotificationService from '../api/notificationService';
import { useAuth } from '../context/authContext';

Modal.setAppElement('#root');

const StoryList: React.FC = () => {
  const { user } = useAuth();
  const [stories, setStories] = useState<Story[]>([]);
  const [projects, setProjects] = useState<Project[]>([]);
  const [users, setUsers] = useState<User[]>([]);
  const [newStory, setNewStory] = useState<Omit<Story, '_id' | 'createdAt'>>({
    name: '',
    description: '',
    priority: 'low',
    project: '',
    status: 'todo',
    owner: ''
  });
  const [editingStory, setEditingStory] = useState<Story | null>(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [detailsModalIsOpen, setDetailsModalIsOpen] = useState(false);
  const [storyDetails, setStoryDetails] = useState<Story | null>(null);

  useEffect(() => {
    loadStories();
    loadProjects();
    loadUsers();
  }, []);

  const loadStories = async () => {
    try {
      const data = await getStories();
      setStories(data);
    } catch (error) {
      console.error('Error loading stories:', error);
    }
  };

  const loadProjects = async () => {
    try {
      const data = await getProjects();
      setProjects(data);
    } catch (error) {
      console.error('Error loading projects:', error);
    }
  };

  const loadUsers = async () => {
    try {
      const data = await getUsers();
      setUsers(data);
    } catch (error) {
      console.error('Error loading users:', error);
    }
  };

  const handleCreateStory = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!newStory.name || !newStory.description || !newStory.project || !newStory.owner) return;
    try {
      const createdStory = await createStory({ ...newStory, createdAt: new Date() });
      setStories([...stories, createdStory]);
      setNewStory({
        name: '',
        description: '',
        priority: 'low',
        project: '',
        status: 'todo',
        owner: ''
      });
      if (user && createdStory.owner === user.id) {
        NotificationService.send({
          title: 'New Story Assigned',
          message: `You have been assigned to a new story: ${createdStory.name}.`,
          date: new Date().toISOString(),
          priority: 'medium',
          read: false
        });
      }
    } catch (error) {
      console.error('Error creating story:', error);
    }
  };

  const handleUpdateStory = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!editingStory) return;
    try {
      const updatedStory = await updateStory(editingStory._id!, editingStory);
      const updatedStories = stories.map(story => (story._id === updatedStory._id ? updatedStory : story));
      setStories(updatedStories);
      setEditingStory(null);
      setModalIsOpen(false);
      if (user && updatedStory.owner === user.id) {
        NotificationService.send({
          title: 'Story Updated',
          message: `A story assigned to you has been updated: ${updatedStory.name}.`,
          date: new Date().toISOString(),
          priority: 'medium',
          read: false
        });
      }
    } catch (error) {
      console.error('Error updating story:', error);
    }
  };

  const handleDeleteStory = async (id: string) => {
    try {
      await deleteStory(id);
      const filteredStories = stories.filter(story => story._id !== id);
      setStories(filteredStories);
    } catch (error) {
      console.error('Error deleting story:', error);
    }
  };

  const openModal = (story: Story) => {
    setEditingStory(story);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setEditingStory(null);
  };

  const openDetailsModal = (story: Story) => {
    setStoryDetails(story);
    setDetailsModalIsOpen(true);
  };

  const closeDetailsModal = () => {
    setDetailsModalIsOpen(false);
    setStoryDetails(null);
  };

  return (
    <div className="container mx-auto p-4">
      <form onSubmit={handleCreateStory} className="mb-4 bg-gray-200 p-4 border border-black rounded">
        <input
          type="text"
          placeholder="Story Name"
          value={newStory.name}
          onChange={(e) => setNewStory({ ...newStory, name: e.target.value })}
          className="border p-2 mb-2 w-full"
        />
        <input
          type="text"
          placeholder="Story Description"
          value={newStory.description}
          onChange={(e) => setNewStory({ ...newStory, description: e.target.value })}
          className="border p-2 mb-2 w-full"
        />
        <select
          value={newStory.priority}
          onChange={(e) => setNewStory({ ...newStory, priority: e.target.value as 'low' | 'medium' | 'high' })}
          className="border p-2 mb-2 w-full"
        >
          <option value="low">Low</option>
          <option value="medium">Medium</option>
          <option value="high">High</option>
        </select>
        <select
          value={newStory.project}
          onChange={(e) => setNewStory({ ...newStory, project: e.target.value })}
          className="border p-2 mb-2 w-full"
        >
          <option value="">Select Project</option>
          {projects.map((project) => (
            <option key={project._id} value={project._id}>{project.name}</option>
          ))}
        </select>
        <select
          value={newStory.owner}
          onChange={(e) => setNewStory({ ...newStory, owner: e.target.value })}
          className="border p-2 mb-2 w-full"
        >
          <option value="">Select Owner</option>
          {users.map((user) => (
            <option key={user._id} value={user._id}>{`${user.firstName}`}</option>
          ))}
        </select>
        <button type="submit" className="bg-blue-500 text-white p-2 rounded">
          Create Story
        </button>
      </form>
      
      {/* Story List */}
      <table className="min-w-full bg-white">
        <thead>
          <tr>
            <th className="py-2">Name</th>
            <th className="py-2">Description</th>
            <th className="py-2">Priority</th>
            <th className="py-2">Project</th>
            <th className="py-2">Owner</th>
            <th className="py-2">Status</th>
            <th className="py-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {stories.map((story) => (
            <tr key={story._id}>
              <td className="border px-4 py-2">{story.name}</td>
              <td className="border px-4 py-2">{story.description}</td>
              <td className="border px-4 py-2">{story.priority}</td>
              <td className="border px-4 py-2">{story.project}</td>
              <td className="border px-4 py-2">{story.owner}</td>
              <td className="border px-4 py-2">{story.status}</td>
              <td className="border px-4 py-2">
                <button onClick={() => openDetailsModal(story)} className="bg-green-500 text-white p-1 rounded mr-2">
                  View
                </button>
                <button onClick={() => openModal(story)} className="bg-yellow-500 text-white p-1 rounded mr-2">
                  Edit
                </button>
                <button onClick={() => handleDeleteStory(story._id!)} className="bg-red-500 text-white p-1 rounded">
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      
      {/* Edit Modal */}
      {modalIsOpen && editingStory && (
        <Modal isOpen={modalIsOpen} onRequestClose={closeModal} contentLabel="Edit Story">
          <form onSubmit={handleUpdateStory}>
            <input
              type="text"
              placeholder="Story Name"
              value={editingStory.name}
              onChange={(e) => setEditingStory({ ...editingStory, name: e.target.value })}
              className="border p-2 mb-2 w-full"
            />
            <input
              type="text"
              placeholder="Story Description"
              value={editingStory.description}
              onChange={(e) => setEditingStory({ ...editingStory, description: e.target.value })}
              className="border p-2 mb-2 w-full"
            />
            <select
              value={editingStory.priority}
              onChange={(e) => setEditingStory({ ...editingStory, priority: e.target.value as 'low' | 'medium' | 'high' })}
              className="border p-2 mb-2 w-full"
            >
              <option value="low">Low</option>
              <option value="medium">Medium</option>
              <option value="high">High</option>
            </select>
            <select
              value={editingStory.project}
              onChange={(e) => setEditingStory({ ...editingStory, project: e.target.value })}
              className="border p-2 mb-2 w-full"
            >
              <option value="">Select Project</option>
              {projects.map((project) => (
                <option key={project._id} value={project._id}>{project.name}</option>
              ))}
            </select>
            <select
              value={editingStory.owner}
              onChange={(e) => setEditingStory({ ...editingStory, owner: e.target.value })}
              className="border p-2 mb-2 w-full"
            >
              <option value="">Select Owner</option>
              {users.map((user) => (
                <option key={user._id} value={user._id}>{`${user.firstName}`}</option>
              ))}
            </select>
            <select
              value={editingStory.status}
              onChange={(e) => setEditingStory({ ...editingStory, status: e.target.value as 'todo' | 'doing' | 'done' })}
              className="border p-2 mb-2 w-full"
            >
              <option value="todo">To Do</option>
              <option value="in-progress">In Progress</option>
              <option value="done">Done</option>
            </select>
            <button type="submit" className="bg-blue-500 text-white p-2 rounded">
              Update Story
            </button>
          </form>
        </Modal>
      )}
      
      {/* Details Modal */}
      {detailsModalIsOpen && storyDetails && (
        <Modal isOpen={detailsModalIsOpen} onRequestClose={closeDetailsModal} contentLabel="Story Details">
          <h2>{storyDetails.name}</h2>
          <p>{storyDetails.description}</p>
          <p>Priority: {storyDetails.priority}</p>
          <p>Project: {storyDetails.project}</p>
          <p>Owner: {storyDetails.owner}</p>
          <p>Status: {storyDetails.status}</p>
        </Modal>
      )}
    </div>
  );
};

export default StoryList;
