import React, { useState, useEffect } from 'react';
import {
  createStory,
  getActiveProject,
  getProjectStories,
  Story,
  updateStory,
  deleteStory
} from '../api/storyService';
import { useAuth } from '../contexts/authContext';
import NotificationService from '../api/notificationService';

const StoryList: React.FC = () => {
  const [stories, setStories] = useState<Story[]>([]);
  const [newStory, setNewStory] = useState<Omit<Story, '_id'>>({ title: '', description: '', status: 'Not Started', priority: 'Medium' });
  const [editingStory, setEditingStory] = useState<Story | null>(null);
  const [activeProjectId, setActiveProjectId] = useState<string | null>(null);
  const { user } = useAuth();

  useEffect(() => {
    loadActiveProjectAndStories();
  }, []);

  const loadActiveProjectAndStories = async () => {
    try {
      const activeProject = await getActiveProject();
      if (activeProject) {
        setActiveProjectId(activeProject._id!);
        const projectStories = await getProjectStories(activeProject._id!);
        setStories(projectStories);
      }
    } catch (error) {
      console.error('Error loading active project and stories:', error);
    }
  };

  const handleCreateStory = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!newStory.title || !newStory.description) return;
    if (!activeProjectId) {
      console.error('No active project selected');
      return;
    }

    try {
      const createdStory = await createStory(activeProjectId, newStory);
      setStories([...stories, createdStory]);
      setNewStory({ title: '', description: '', status: 'Not Started', priority: 'Medium' });
      NotificationService.send({
        title: 'New Story Created',
        message: `Story "${newStory.title}" has been created in project with ID ${activeProjectId}.`,
        date: new Date().toISOString(),
        priority: 'medium',
        read: false
      });
    } catch (error) {
      console.error('Error creating story:', error);
    }
  };

  const handleUpdateStory = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!editingStory) return;

    try {
      await updateStory(activeProjectId!, editingStory._id!, editingStory);
      setStories(stories.map((s) => (s._id === editingStory._id ? editingStory : s)));
      setEditingStory(null);
    } catch (error) {
      console.error('Error updating story:', error);
    }
  };

  const handleDeleteStory = async (storyId: string) => {
    try {
      await deleteStory(activeProjectId!, storyId);
      setStories(stories.filter((s) => s._id !== storyId));
    } catch (error) {
      console.error('Error deleting story:', error);
    }
  };

  const startEditingStory = (story: Story) => {
    setEditingStory(story);
  };

  const cancelEditing = () => {
    setEditingStory(null);
  };

  return (
    <div className="container mx-auto p-4">
      <form onSubmit={handleCreateStory} className="mb-4 bg-gray-200 p-4 border border-black rounded">
        <input
          type="text"
          placeholder="Story Title"
          value={newStory.title}
          onChange={(e) => setNewStory({ ...newStory, title: e.target.value })}
          className="border p-2 mb-2 w-full"
        />
        <input
          type="text"
          placeholder="Story Description"
          value={newStory.description}
          onChange={(e) => setNewStory({ ...newStory, description: e.target.value })}
          className="border p-2 mb-2 w-full"
        />
        <select
          value={newStory.status}
          onChange={(e) => setNewStory({ ...newStory, status: e.target.value })}
          className="border p-2 mb-2 w-full"
        >
          <option value="Not Started">Not Started</option>
          <option value="In Progress">In Progress</option>
          <option value="Completed">Completed</option>
        </select>
        <select
          value={newStory.priority}
          onChange={(e) => setNewStory({ ...newStory, priority: e.target.value })}
          className="border p-2 mb-2 w-full"
        >
          <option value="Low">Low</option>
          <option value="Medium">Medium</option>
          <option value="High">High</option>
        </select>
        <button type="submit" className="bg-blue-500 text-white p-2 rounded">Add Story</button>
      </form>

      <ul>
        {stories.map((story) => (
          <li key={story._id} className="border p-2 mb-2 bg-gray-200">
            <strong>{story.title}</strong>: {story.description} ({story.status})
            <button onClick={() => startEditingStory(story)} className="bg-yellow-500 text-white p-2 ml-2 rounded">Edit</button>
            <button onClick={() => handleDeleteStory(story._id!)} className="bg-red-500 text-white p-2 ml-2 rounded">Delete</button>
          </li>
        ))}
      </ul>

      {editingStory && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
          <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-md mx-4">
            <h2 className="text-2xl mb-4">Edit Story</h2>
            <form onSubmit={handleUpdateStory} className="mb-4">
              <input
                type="text"
                placeholder="Story Title"
                value={editingStory.title}
                onChange={(e) => setEditingStory({ ...editingStory, title: e.target.value })}
                className="border p-2 mb-2 w-full"
              />
              <input
                type="text"
                placeholder="Story Description"
                value={editingStory.description}
                onChange={(e) => setEditingStory({ ...editingStory, description: e.target.value })}
                className="border p-2 mb-2 w-full"
              />
              <select
                value={editingStory.status}
                onChange={(e) => setEditingStory({ ...editingStory, status: e.target.value })}
                className="border p-2 mb-2 w-full"
              >
                <option value="Not Started">Not Started</option>
                <option value="In Progress">In Progress</option>
                <option value="Completed">Completed</option>
              </select>
              <select
                value={editingStory.priority}
                onChange={(e) => setEditingStory({ ...editingStory, priority: e.target.value })}
                className="border p-2 mb-2 w-full"
              >
                <option value="Low">Low</option>
                <option value="Medium">Medium</option>
                <option value="High">High</option>
              </select>
              <button type="submit" className="bg-green-500 text-white p-2 rounded">Save Changes</button>
              <button type="button" onClick={cancelEditing} className="bg-gray-500 text-white p-2 ml-2 rounded">Cancel</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default StoryList;
