// src/components/Navbar.tsx
import React, { useContext, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import AuthContext from '../context/authContext';
import NotificationService from '../api/NotificationService';
import { FaBell } from 'react-icons/fa';

const Navbar: React.FC = () => {
  const { token, logout } = useContext(AuthContext);
  const [unreadCount, setUnreadCount] = useState<number>(0);
  const [notifications, setNotifications] = useState<Notification[]>([]);

  useEffect(() => {
    const subscription = NotificationService.getUnreadCount().subscribe(setUnreadCount);
    const notificationSubscription = NotificationService.list().subscribe(setNotifications);
    return () => {
      subscription.unsubscribe();
      notificationSubscription.unsubscribe();
    };
  }, []);

  return (
    <nav className="bg-black text-white py-3 px-5 fixed top-0 w-full flex justify-between items-center">
      <div className="text-cyan-400 font-bold text-xl">
        <Link to="/">ManageMe</Link>
      </div>
      <ul className="flex items-center space-x-4">
        {token ? (
          <>
            <li>
              <Link to="/" className="hover:bg-gray-700 py-2 px-4 rounded">Project List</Link>
            </li>
            <li>
              <Link to="/active" className="hover:bg-gray-700 py-2 px-4 rounded">Active Project</Link>
            </li>
            <li>
              <Link to="/stories" className="hover:bg-gray-700 py-2 px-4 rounded">Stories</Link>
            </li>
            <li>
              <Link to="/tasks" className="hover:bg-gray-700 py-2 px-4 rounded">Tasks</Link>
            </li>
            <li className="relative">
              <button className="hover:bg-gray-700 py-2 px-4 rounded relative">
                <FaBell />
                {unreadCount > 0 && (
                  <span className="absolute top-0 right-0 bg-red-500 text-white rounded-full text-xs w-6 h-6 flex items-center justify-center">
                    {unreadCount}
                  </span>
                )}
              </button>
              {/* Notification Dropdown */}
              {notifications.length > 0 && (
                <div className="absolute right-0 mt-2 w-64 bg-white text-black shadow-lg rounded-lg">
                  <ul>
                    {notifications.map((notification, index) => (
                      <li key={index} className="border-b p-2">
                        <div className="font-bold">{notification.title}</div>
                        <div>{notification.message}</div>
                        <div className="text-xs text-gray-500">{new Date(notification.date).toLocaleString()}</div>
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </li>
            <li>
              <button onClick={logout} className="hover:bg-gray-700 py-2 px-4 rounded">Logout</button>
            </li>
          </>
        ) : (
          <li>
            <Link to="/login" className="hover:bg-gray-700 py-2 px-4 rounded">Login</Link>
          </li>
        )}
      </ul>
    </nav>
  );
};

export default Navbar;
