import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import { Story, getStories, createStory, updateStory, deleteStory, setActiveStory, getActiveStory } from '../api/storyService';
import { getProjects, Project } from '../api/projectService';
import { getUsers, User } from '../api/userService';

Modal.setAppElement('#root');

const StoryList: React.FC = () => {
  const [stories, setStories] = useState<Story[]>([]);
  const [projects, setProjects] = useState<Project[]>([]);
  const [users, setUsers] = useState<User[]>([]);
  const [newStory, setNewStory] = useState<Omit<Story, 'id'>>({
    name: '',
    description: '',
    priority: 'low',
    project: '',
    status: 'todo',
    owner: '',
  });
  const [editingStory, setEditingStory] = useState<Story | null>(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedStory, setSelectedStory] = useState<Story | null>(null);

  useEffect(() => {
    loadStories();
    loadProjects();
    loadUsers();
  }, []);

  const loadStories = async () => {
    try {
      const data = await getStories();
      setStories(data);
    } catch (error) {
      console.error('Error loading stories:', error);
    }
  };

  const loadProjects = async () => {
    try {
      const data = await getProjects();
      setProjects(data);
    } catch (error) {
      console.error('Error loading projects:', error);
    }
  };

  const loadUsers = async () => {
    try {
      const data = await getUsers();
      setUsers(data);
    } catch (error) {
      console.error('Error loading users:', error);
    }
  };

  const handleCreateStory = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!newStory.name || !newStory.description || !newStory.project || !newStory.owner) {
      console.error('All fields are required');
      return;
    }
    try {
      await createStory({ ...newStory, createdAt: new Date() });
      setNewStory({
        name: '',
        description: '',
        priority: 'low',
        project: '',
        status: 'todo',
        owner: '',
      });
      loadStories();
    } catch (error) {
      console.error('Error creating story:', error);
    }
  };

  const handleUpdateStory = async (e: React.FormEvent) => {
    e.preventDefault();
    if (editingStory) {
      try {
        await updateStory(editingStory);
        setEditingStory(null);
        setModalIsOpen(false);
        loadStories();
      } catch (error) {
        console.error('Error updating story:', error);
      }
    }
  };

  const handleDeleteStory = async (id: string) => {
    try {
      await deleteStory(id);
      loadStories();
    } catch (error) {
      console.error('Error deleting story:', error);
    }
  };

  const handleShowDetails = (story: Story) => {
    setSelectedStory(story);
    setModalIsOpen(true);
  };

  const openModal = (story: Story) => {
    setEditingStory(story);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setEditingStory(null);
    setSelectedStory(null);
    setModalIsOpen(false);
  };

  const handleSetActiveStory = async (id: string) => {
    try {
      await setActiveStory(id);
      loadStories();
    } catch (error) {
      console.error('Error setting active story:', error);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <form onSubmit={handleCreateStory} className="mb-4 bg-gray-200 p-4 border border-black rounded">
        <input
          type="text"
          placeholder="Story Name"
          value={newStory.name}
          onChange={(e) => setNewStory({ ...newStory, name: e.target.value })}
          className="border p-2 mb-2 w-full"
        />
        <input
          type="text"
          placeholder="Story Description"
          value={newStory.description}
          onChange={(e) => setNewStory({ ...newStory, description: e.target.value })}
          className="border p-2 mb-2 w-full"
        />
        <select
          value={newStory.project}
          onChange={(e) => setNewStory({ ...newStory, project: e.target.value })}
          className="border p-2 mb-2 w-full"
        >
          <option value="" disabled>Select Project</option>
          {projects.map((project) => (
            <option key={project._id} value={project._id}>{project.name}</option>
          ))}
        </select>
        <select
          value={newStory.owner}
          onChange={(e) => setNewStory({ ...newStory, owner: e.target.value })}
          className="border p-2 mb-2 w-full"
        >
          <option value="" disabled>Select Owner</option>
          {users.map((user) => (
            <option key={user._id} value={user._id}>{user.name}</option>
          ))}
        </select>
        <select
          value={newStory.priority}
          onChange={(e) => setNewStory({ ...newStory, priority: e.target.value })}
          className="border p-2 mb-2 w-full"
        >
          <option value="low">Low</option>
          <option value="medium">Medium</option>
          <option value="high">High</option>
        </select>
        <button type="submit" className="bg-blue-500 text-white p-2 rounded">Add Story</button>
      </form>

      <div className="grid grid-cols-3 gap-4">
        <div>
          <h2>Todo</h2>
          {stories.filter(story => story.status === 'todo').map(story => (
            <div key={story._id} className="border p-2 mb-2 bg-gray-200">
              <strong>{story.name}</strong>: {story.description}
              <button onClick={() => openModal(story)} className="bg-yellow-500 text-white p-2 ml-2 rounded">Edit</button>
              <button onClick={() => handleDeleteStory(story._id!)} className="bg-red-500 text-white p-2 ml-2 rounded">Delete</button>
              <button onClick={() => handleShowDetails(story)} className="bg-green-500 text-white p-2 ml-2 rounded">Details</button>
              <button onClick={() => handleSetActiveStory(story._id!)} className={`p-2 ml-2 rounded ${story.active ? 'bg-green-500' : 'bg-gray-500'} text-white`}>
                {story.active ? 'Selected' : 'Select'}
              </button>
            </div>
          ))}
        </div>
        <div>
          <h2>Doing</h2>
          {stories.filter(story => story.status === 'doing').map(story => (
            <div key={story._id} className="border p-2 mb-2 bg-gray-200">
              <strong>{story.name}</strong>: {story.description}
              <button onClick={() => openModal(story)} className="bg-yellow-500 text-white p-2 ml-2 rounded">Edit</button>
              <button onClick={() => handleDeleteStory(story._id!)} className="bg-red-500 text-white p-2 ml-2 rounded">Delete</button>
              <button onClick={() => handleShowDetails(story)} className="bg-green-500 text-white p-2 ml-2 rounded">Details</button>
              <button onClick={() => handleSetActiveStory(story._id!)} className={`p-2 ml-2 rounded ${story.active ? 'bg-green-500' : 'bg-gray-500'} text-white`}>
                {story.active ? 'Selected' : 'Select'}
              </button>
            </div>
          ))}
        </div>
        <div>
          <h2>Done</h2>
          {stories.filter(story => story.status === 'done').map(story => (
            <div key={story._id} className="border p-2 mb-2 bg-gray-200">
              <strong>{story.name}</strong>: {story.description}
              <button onClick={() => openModal(story)} className="bg-yellow-500 text-white p-2 ml-2 rounded">Edit</button>
              <button onClick={() => handleDeleteStory(story._id!)} className="bg-red-500 text-white p-2 ml-2 rounded">Delete</button>
              <button onClick={() => handleShowDetails(story)} className="bg-green-500 text-white p-2 ml-2 rounded">Details</button>
              <button onClick={() => handleSetActiveStory(story._id!)} className={`p-2 ml-2 rounded ${story.active ? 'bg-green-500' : 'bg-gray-500'} text-white`}>
                {story.active ? 'Selected' : 'Select'}
              </button>
            </div>
          ))}
        </div>
      </div>

      <Modal isOpen={modalIsOpen} onRequestClose={closeModal}>
        {editingStory && (
          <form onSubmit={handleUpdateStory} className="mb-4">
            <input
              type="text"
              placeholder="Story Name"
              value={editingStory.name}
              onChange={(e) => setEditingStory({ ...editingStory, name: e.target.value })}
              className="border p-2 mb-2 w-full"
            />
            <input
              type="text"
              placeholder="Story Description"
              value={editingStory.description}
              onChange={(e) => setEditingStory({ ...editingStory, description: e.target.value })}
              className="border p-2 mb-2 w-full"
            />
            <select
              value={editingStory.project}
              onChange={(e) => setEditingStory({ ...editingStory, project: e.target.value })}
              className="border p-2 mb-2 w-full"
            >
              <option value="" disabled>Select Project</option>
              {projects.map((project) => (
                <option key={project._id} value={project._id}>{project.name}</option>
              ))}
            </select>
            <select
              value={editingStory.owner}
              onChange={(e) => setEditingStory({ ...editingStory, owner: e.target.value })}
              className="border p-2 mb-2 w-full"
            >
              <option value="" disabled>Select Owner</option>
              {users.map((user) => (
                <option key={user._id} value={user._id}>{user.name}</option>
              ))}
            </select>
            <select
              value={editingStory.priority}
              onChange={(e) => setEditingStory({ ...editingStory, priority: e.target.value })}
              className="border p-2 mb-2 w-full"
            >
              <option value="low">Low</option>
              <option value="medium">Medium</option>
              <option value="high">High</option>
            </select>
            <button type="submit" className="bg-blue-500 text-white p-2 rounded">Update Story</button>
          </form>
        )}
        {selectedStory && (
          <div>
            <h2>{selectedStory.name}</h2>
            <p>Description: {selectedStory.description}</p>
            <p>Priority: {selectedStory.priority}</p>
            <p>Status: {selectedStory.status}</p>
            <p>Project: {projects.find(project => project._id === selectedStory.project)?.name}</p>
            <p>Owner: {users.find(user => user._id === selectedStory.owner)?.name}</p>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default StoryList;
