import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import { Task, getTasks, createTask, updateTask, deleteTask } from '../api/taskService';
import { Story, getStories } from '../api/storyService';
import { User, getUsers } from '../api/userService';

Modal.setAppElement('#root');

const TaskList: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [stories, setStories] = useState<Story[]>([]);
  const [users, setUsers] = useState<User[]>([]);
  const [newTask, setNewTask] = useState<Omit<Task, '_id' | 'createdAt' | 'startDate' | 'endDate'>>({
    name: '',
    description: '',
    priority: 'low',
    story: '',
    estimatedHours: 0,
    status: 'todo',
    assignedUser: ''
  });
  const [editingTask, setEditingTask] = useState<Task | null>(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [detailsModalIsOpen, setDetailsModalIsOpen] = useState(false);
  const [taskDetails, setTaskDetails] = useState<Task | null>(null);

  useEffect(() => {
    loadTasks();
    loadStories();
    loadUsers();
  }, []);

  const loadTasks = async () => {
    try {
      const data = await getTasks();
      setTasks(data);
    } catch (error) {
      console.error('Error loading tasks:', error);
    }
  };

  const loadStories = async () => {
    try {
      const data = await getStories();
      setStories(data);
    } catch (error) {
      console.error('Error loading stories:', error);
    }
  };

  const loadUsers = async () => {
    try {
      const data = await getUsers();
      setUsers(data);
    } catch (error) {
      console.error('Error loading users:', error);
    }
  };

  const handleCreateTask = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!newTask.name || !newTask.description || !newTask.story || !newTask.assignedUser) return;
    try {
      await createTask({ ...newTask, createdAt: new Date().toISOString() });
      setNewTask({
        name: '',
        description: '',
        priority: 'low',
        story: '',
        estimatedHours: 0,
        status: 'todo',
        assignedUser: ''
      });
      loadTasks();
    } catch (error) {
      console.error('Error creating task:', error);
    }
  };

  const handleUpdateTask = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!editingTask) return;
    try {
      await updateTask(editingTask._id!, editingTask);
      setEditingTask(null);
      setModalIsOpen(false);
      loadTasks();
    } catch (error) {
      console.error('Error updating task:', error);
    }
  };

  const handleDeleteTask = async (id: string) => {
    try {
      await deleteTask(id);
      loadTasks();
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const openModal = (task: Task) => {
    setEditingTask(task);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setEditingTask(null);
  };

  const openDetailsModal = (task: Task) => {
    setTaskDetails(task);
    setDetailsModalIsOpen(true);
  };

  const closeDetailsModal = () => {
    setDetailsModalIsOpen(false);
    setTaskDetails(null);
  };

  return (
    <div className="container mx-auto p-4">
      <form onSubmit={handleCreateTask} className="mb-4 bg-gray-200 p-4 border border-black rounded">
        <input
          type="text"
          placeholder="Task Name"
          value={newTask.name}
          onChange={(e) => setNewTask({ ...newTask, name: e.target.value })}
          className="border p-2 mb-2 w-full"
        />
        <input
          type="text"
          placeholder="Task Description"
          value={newTask.description}
          onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
          className="border p-2 mb-2 w-full"
        />
        <select
          value={newTask.priority}
          onChange={(e) => setNewTask({ ...newTask, priority: e.target.value as 'low' | 'medium' | 'high' })}
          className="border p-2 mb-2 w-full"
        >
          <option value="low">Low</option>
          <option value="medium">Medium</option>
          <option value="high">High</option>
        </select>
        <select
          value={newTask.story}
          onChange={(e) => setNewTask({ ...newTask, story: e.target.value })}
          className="border p-2 mb-2 w-full"
        >
          <option value="">Select Story</option>
          {stories.map((story) => (
            <option key={story._id} value={story._id}>{story.name}</option>
          ))}
        </select>
        <input
          type="number"
          placeholder="Estimated Hours"
          value={newTask.estimatedHours}
          onChange={(e) => setNewTask({ ...newTask, estimatedHours: +e.target.value })}
          className="border p-2 mb-2 w-full"
        />
        <select
          value={newTask.status}
          onChange={(e) => setNewTask({ ...newTask, status: e.target.value as 'todo' | 'doing' | 'done' })}
          className="border p-2 mb-2 w-full"
        >
          <option value="todo">To Do</option>
          <option value="doing">Doing</option>
          <option value="done">Done</option>
        </select>
        <select
          value={newTask.assignedUser}
          onChange={(e) => setNewTask({ ...newTask, assignedUser: e.target.value })}
          className="border p-2 mb-2 w-full"
        >
          <option value="">Select User</option>
          {users.map((user) => (
            <option key={user._id} value={user._id}>{user.firstName}</option>
          ))}
        </select>
        <button type="submit" className="bg-blue-500 text-white p-2 rounded">Add Task</button>
      </form>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {tasks.map((task) => (
          <div key={task._id} className="bg-white p-4 border border-black rounded">
            <h3 className="text-lg font-bold">{task.name}</h3>
            <p>{task.description}</p>
            <p>Priority: {task.priority}</p>
            <p>Estimated Hours: {task.estimatedHours}</p>
            <p>Status: {task.status}</p>
            <button onClick={() => openModal(task)} className="bg-yellow-500 text-white p-2 rounded mr-2">Edit</button>
            <button onClick={() => handleDeleteTask(task._id!)} className="bg-red-500 text-white p-2 rounded">Delete</button>
            <button onClick={() => openDetailsModal(task)} className="bg-green-500 text-white p-2 rounded ml-2">Details</button>
          </div>
        ))}
      </div>

      {modalIsOpen && editingTask && (
        <Modal isOpen={modalIsOpen} onRequestClose={closeModal}>
          <form onSubmit={handleUpdateTask} className="p-4">
            <input
              type="text"
              placeholder="Task Name"
              value={editingTask.name}
              onChange={(e) => setEditingTask({ ...editingTask, name: e.target.value })}
              className="border p-2 mb-2 w-full"
            />
            <input
              type="text"
              placeholder="Task Description"
              value={editingTask.description}
              onChange={(e) => setEditingTask({ ...editingTask, description: e.target.value })}
              className="border p-2 mb-2 w-full"
            />
            <select
              value={editingTask.priority}
              onChange={(e) => setEditingTask({ ...editingTask, priority: e.target.value as 'low' | 'medium' | 'high' })}
              className="border p-2 mb-2 w-full"
            >
              <option value="low">Low</option>
              <option value="medium">Medium</option>
              <option value="high">High</option>
            </select>
            <select
              value={editingTask.story}
              onChange={(e) => setEditingTask({ ...editingTask, story: e.target.value })}
              className="border p-2 mb-2 w-full"
            >
              <option value="">Select Story</option>
              {stories.map((story) => (
                <option key={story._id} value={story._id}>{story.name}</option>
              ))}
            </select>
            <input
              type="number"
              placeholder="Estimated Hours"
              value={editingTask.estimatedHours}
              onChange={(e) => setEditingTask({ ...editingTask, estimatedHours: +e.target.value })}
              className="border p-2 mb-2 w-full"
            />
            <select
              value={editingTask.status}
              onChange={(e) => setEditingTask({ ...editingTask, status: e.target.value as 'todo' | 'doing' | 'done' })}
              className="border p-2 mb-2 w-full"
            >
              <option value="todo">To Do</option>
              <option value="doing">Doing</option>
              <option value="done">Done</option>
            </select>
            <select
              value={editingTask.assignedUser}
              onChange={(e) => setEditingTask({ ...editingTask, assignedUser: e.target.value })}
              className="border p-2 mb-2 w-full"
            >
              <option value="">Select User</option>
              {users.map((user) => (
                <option key={user._id} value={user._id}>{user.firstName}</option>
              ))}
            </select>
            <button type="submit" className="bg-blue-500 text-white p-2 rounded">Update Task</button>
            <button onClick={closeModal} className="bg-gray-500 text-white p-2 rounded ml-2">Cancel</button>
          </form>
        </Modal>
      )}

      {detailsModalIsOpen && taskDetails && (
        <Modal isOpen={detailsModalIsOpen} onRequestClose={closeDetailsModal}>
          <div className="p-4">
            <h2 className="text-lg font-bold">{taskDetails.name}</h2>
            <p>{taskDetails.description}</p>
            <p>Priority: {taskDetails.priority}</p>
            <p>Estimated Hours: {taskDetails.estimatedHours}</p>
            <p>Status: {taskDetails.status}</p>
            <p>Story: {stories.find(story => story._id === taskDetails.story)?.name}</p>
            <p>Assigned User: {users.find(user => user._id === taskDetails.assignedUser)?.firstName}</p>
            <p>Start Date: {taskDetails.startDate}</p>
            <p>End Date: {taskDetails.endDate}</p>
            <button onClick={closeDetailsModal} className="bg-gray-500 text-white p-2 rounded">Close</button>
          </div>
        </Modal>
      )}
    </div>
  );
};

export default TaskList;
