// src/App.tsx
import React, { useContext, useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import ProjectList from './components/ProjectList';
import ActiveProject from './components/ActiveProject';
import StoryList from './components/storyList';
import Login from './components/Login';
import { AuthProvider } from './context/authContext';
import AuthContext from './context/authContext';
import './index.css';
import TaskList from './components/taskList';

const PrivateRoute: React.FC<{ element: React.ReactElement }> = ({ element }) => {
  const { token } = useContext(AuthContext);
  return token ? element : <Navigate to="/login" />;
};

const App: React.FC = () => {
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    const savedMode = localStorage.getItem('darkMode');
    if (savedMode) {
      setDarkMode(JSON.parse(savedMode));
    }
  }, []);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
    localStorage.setItem('darkMode', JSON.stringify(!darkMode));
  };

  return (
    <AuthProvider>
      <Router>
        <div className={darkMode ? 'dark' : ''}>
          <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-black dark:text-white">
            <Navbar />
            <div className="pt-16">
              <button
                onClick={toggleDarkMode}
                className="fixed top-20 right-5 bg-blue-500 text-white p-2 rounded"
              >
                {darkMode ? 'Light Mode' : 'Dark Mode'}
              </button>
              <Routes>
                <Route path="/login" element={<Login />} />
                <Route path="/" element={<PrivateRoute element={<ProjectList />} />} />
                <Route path="/active" element={<PrivateRoute element={<ActiveProject />} />} />
                <Route path="/stories" element={<PrivateRoute element={<StoryList />} />} />
                <Route path="/tasks" element={<PrivateRoute element={<TaskList />} />} />
              </Routes>
            </div>
          </div>
        </div>
      </Router>
    </AuthProvider>
  );
};

export default App;
