import axios from 'axios';

export interface Story {
  _id?: string;
  name: string;
  description: string;
  priority: 'low' | 'medium' | 'high';
  project: string;
  createdAt?: Date;
  state: 'todo' | 'doing' | 'done';
  owner: string;
}

export const getStories = async () => {
  const response = await axios.get<Story[]>('/stories');
  return response.data;
};

export const createStory = async (story: Omit<Story, '_id' | 'createdAt'>) => {
  const response = await axios.post<Story>('/stories', story);
  return response.data;
};

export const updateStory = async (id: string, story: Partial<Omit<Story, '_id' | 'createdAt'>>) => {
  const response = await axios.put<Story>(`/stories/${id}`, story);
  return response.data;
};

export const deleteStory = async (id: string) => {
  await axios.delete(`/stories/${id}`);
};
