import axios from 'axios';

const apiClient = axios.create({
  baseURL: 'http://localhost:5000/api',
  headers: {
    'Content-Type': 'application/json',
  },
});

export interface Project {
  _id?: string;
  name: string;
  description: string;
}

export const getProjects = async (): Promise<Project[]> => {
  const response = await apiClient.get('/projects');
  return response.data;
};

export const createProject = async (project: Omit<Project, '_id'>): Promise<Project> => {
  const response = await apiClient.post('/projects', project);
  return response.data;
};

export const updateProject = async (id: string, project: Project): Promise<Project> => {
  try {
    const response = await apiClient.put(`/projects/${id}`, project);
    return response.data;
  } catch (error) {
    console.error('Error updating project:', error);
    throw error;
  }
};

export const deleteProject = async (id: string): Promise<void> => {
  try {
    await apiClient.delete(`/projects/${id}`);
  } catch (error) {
    console.error('Error deleting project:', error);
    throw error;
  }
};

export const setActiveProject = async (id: string): Promise<void> => {
  try {
    await apiClient.post(`/projects/active/${id}`);
  } catch (error) {
    console.error('Error setting active project:', error);
    throw error;
  }
};

export const getActiveProject = async (): Promise<Project> => {
  const response = await apiClient.get('/projects/active');
  return response.data;
};
