// src/components/NotificationList.tsx
import React, { useEffect, useState } from 'react';
import NotificationService, { Notification } from '../api/notificationService';

const NotificationList: React.FC = () => {
  const [notifications, setNotifications] = useState<Notification[]>([]);

  useEffect(() => {
    const subscription = NotificationService.list().subscribe(setNotifications);
    return () => subscription.unsubscribe();
  }, []);

  const markAsRead = (notification: Notification) => {
    NotificationService.markAsRead(notification);
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl mb-4">Notifications</h1>
      <ul>
        {notifications.map((notification, index) => (
          <li key={index} className={`border p-2 mb-2 ${notification.read ? 'bg-gray-300' : 'bg-gray-100'}`}>
            <strong>{notification.title}</strong>
            <p>{notification.message}</p>
            <p className="text-xs">{new Date(notification.date).toLocaleString()}</p>
            {!notification.read && (
              <button onClick={() => markAsRead(notification)} className="bg-blue-500 text-white p-1 rounded">
                Mark as Read
              </button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default NotificationList;
